since_id.map:
function(doc) {
	if(doc.id && doc.text && doc.party) emit(parseInt(doc._id), parseInt(doc.id));
}

tweets.map:
function(doc) {
//"id_str": "242300291976949760",
  emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": doc.party, "text": doc.text});
}

democrats.map:
function(doc) {
  if(doc.party === "democrats") emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": "d", "text": doc.text});
}

republican.map:
function(doc) {
  if(doc.party === "republican") emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": "r", "text": doc.text});
}

all.map:
function(doc) {
//"id_str": "242300291976949760",
  emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": doc.party, "text": doc.text});
}

highscores.map:
function(doc) {
  if(doc.user && doc.score && doc.party) emit(doc.score, {user: doc.user, score: doc.score, party: doc.party});
}

cumscore_r.map:
function(doc) {
  if(doc.party && doc.score && doc.user && doc.party === 'r') emit(parseInt(doc.score), parseInt(doc.score));
}

cumscore_d.map:
function(doc) {
  if(doc.party && doc.score && doc.user && doc.party === 'd') emit(parseInt(doc.score), parseInt(doc.score));
}
