democrats.map:
function(doc) {
  if(doc.type === 'tweet' && doc.party === "democrats") emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": "d", "text": doc.text});
}

republican.map:
function(doc) {
  if(doc.type === 'tweet' && doc.party === "republican") emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": "r", "text": doc.text});
}

all_tweets.map:
function(doc) {
if(doc.type === 'tweet') emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": doc.party, "text": doc.text});
}

highscores.map:
function(doc) {
  if(doc.type === 'score') emit([doc.score, doc.timestamp], {user: doc.user, score: doc.score, party: doc.party, timestamp: doc.timestamp});
}
