democrats.map:
function(doc) {
  if(doc.type === 'tweet' && doc.party === "democrats") emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": "d", "text": doc.text});
}

republican.map:
function(doc) {
  if(doc.type === 'tweet' && doc.party === "republican") emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": "r", "text": doc.text});
}

all_tweets.map:
function(doc) {
if(doc.type === 'tweet') emit(doc.id, {"name": doc.user.name, "screen_name": doc.user.screen_name, "party": doc.party, "text": doc.text});
}

highscores.map:
function(doc) {
  if(doc.type === 'score' && doc.score > 0) emit([doc.score, Date.parse(new Date(doc.timestamp).toDateString()), doc.timestamp], {user: doc.user, score: doc.score, party: doc.party, timestamp: doc.timestamp});
}

highscores_by_timestamp.map:
function(doc) {
  if(doc.type === 'score' && doc.score > 0) emit([Date.parse(new Date(doc.timestamp).toDateString()), doc.score, doc.timestamp], {user: doc.user, score: doc.score, party: doc.party, timestamp: doc.timestamp});
}

cumscore_d_by_day.map:
function(doc) {
  if(doc.type === 'score' && doc.party === 'd') emit(Date.parse(new Date(doc.timestamp).toDateString()), parseInt(doc.score));
}

cumscore_r_by_day.map:
function(doc) {
  if(doc.type === 'score' && doc.party === 'r') emit(Date.parse(new Date(doc.timestamp).toDateString()), parseInt(doc.score));
}

cumscore.map:
function(doc) {
  if(doc.type === 'score') emit([doc.party, Date.parse(new Date(doc.timestamp).toDateString())], parseInt(doc.score));
}
